import React, {Component} from 'react';
import {BrowserRouter as Router, Link, Route} from "react-router-dom";

class SignInForm extends Component {
    constructor(){
        super();

        this.state = {
            email: '',
            password: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e){
        let target = e.target;
        let value = target.type === 'checkbox' ? target.checked : target.value;
        let name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(e){
        e.preventDefault();

        console.log('The form was submitted with the following data:');
        console.log(this.state);
    }

    render() {
        return (
            <div className="FormCenter">
                <form className="FormFields" onSubmit={this.handleSubmit}>

                    {/*// Email*/}
                    <div className="FormField">
                        <label className="FormField__Label" htmlFor="email">E-mail Address</label> <br/>
                        <input type="email" className="FormField__Input" placeholder="Enter your email" name="email"/>
                    </div>


                    {/*//Password*/}
                    <div className="FormField">
                        <label className="FormField__Label" htmlFor="password">Password</label> <br/>
                        <input type="password" className="FormField__Input" placeholder="Enter your password" name="password"/>
                    </div>



                    <div className="FormField">
                        <button className="FormField__Button mr-20">Sign In</button>
                        <Link to="/" className="FormField__Link">Create an account</Link>
                    </div>

                </form>
            </div>
        );
    }
}

export default SignInForm;
